import org.apache.spark.sql.types.{StructType,StructField,StringType,IntegerType,DoubleType};
import org.apache.spark.sql.Row;


val inRDD = inDF.rdd.map(row => (row.getDouble(0), row.getInt(1), row.getInt(2), row.getInt(3), row.getInt(4)))
val transferRDD = inRDD.map{case(price,lotsize,bedrooms,bathrms,stories) => (price, scala.math.pow(bedrooms.toDouble,3).toInt, bedrooms*bathrms, stories, scala.math.sqrt(lotsize.toDouble).toInt)}

//val schema = new StructType(Array(StructField("price", DoubleType, nullable = true),StructField("bedrooms", IntegerType, nullable = true),StructField("bedandbathrooms", IntegerType, nullable = true),StructField("lotsize", IntegerType, nullable = true),StructField("stories", IntegerType, nullable = true)))
val schema = new StructType(Array(StructField("price", DoubleType, nullable = true),StructField("bedrooms", IntegerType, nullable = true),StructField("bedandbathrooms", IntegerType, nullable = true),StructField("stories", IntegerType, nullable = true),StructField("lotsize", IntegerType, nullable = true)))
val rowRDD = transferRDD.map{case(price,bedrooms,bedandbathrooms,stories,lotsize) => Row(price,bedrooms,bedandbathrooms,stories,lotsize)}

val outDF = sqlContext.createDataFrame(rowRDD, schema)


outDF.registerTempTable("outDF")
