import org.apache.spark.sql._

val rddRecords = inDF.rdd.map(row => Seq(row.getInt(0)))
val nrows = rddRecords.count().toInt

val byColumnAndRow = rddRecords.zipWithIndex.flatMap {
      case (row, rowIndex) => row.zipWithIndex.map {
        case (number, columnIndex) => columnIndex ->(rowIndex, number)}}

val byColumn = byColumnAndRow.groupByKey.sortByKey().values

val transposed = byColumn.map {
    indexedRow => indexedRow.toSeq.sortBy(_._1).map(_._2)}
val transposedRow = transposed.map(x => Row.fromSeq(x))

val schema = new StructType(Array(StructField("row_1", IntegerType, nullable = true),StructField("row_2", IntegerType, nullable = true), StructField("row_3", IntegerType, nullable = true), StructField("row_4", IntegerType, nullable = true), StructField("row_5", IntegerType, nullable = true), StructField("row_6", IntegerType, nullable = true), StructField("row_7", IntegerType, nullable = true), StructField("row_8", IntegerType, nullable = true), StructField("row_9", IntegerType, nullable = true), StructField("row_10", IntegerType, nullable = true)));
//var arr = Array[StructField]()
//for (i <- 1 to nrows) {
  //    arr = arr :+ StructField("row_" + i, IntegerType, true)}
//val schema = StructType(arr)

val outDF = sqlContext.createDataFrame(transposedRow, schema)
outDF.registerTempTable("outDF")
